import React from "react";
import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import ya from "axios";


const Register = () => {
    const [inputs, setInputs] = useState({
        username: "",
        email: "",
        password: "",
      });
      const [err, setError] = useState(null);
    
      const navigate = useNavigate();

      const handleChange = (e) => {
        setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
      };

      const handleSubmit = async (e) => {
        e.preventDefault();
        try {
          await axios.post("/register", inputs);
          navigate("/login");
        } catch (err) {
          setError(err.response.data);
        }
      };

    return(
    <div className="auth">
        <h1>Register</h1>
        <form>
            <input require type="text" placeholder='username' onChange={handleChange}  /> 
            <input require type="email" placeholder='email' onChange={handleChange} />
            <input require type="password" placeholder='password' onChange={handleChange} /> 
            <button onClick={handleSubmit}>Register</button>
            <p>This is an error!</p>
            <span>Don't you have an account <Link to="/login">Login</Link></span>
        </form>
    </div>
    )
    
}
export default Register
